import React,{useState} from 'react';
import { StyleSheet, Text, View,Button, Alert,Image } from 'react-native';
import  expo from'./app.json'

export default function App() {


  const onPressLearnMore=()=>{
    console.log('hi',)
    Alert.alert('Right')
  }



  let audio =  new Audio(require('./assets/beep.wav'));
  audio.type='audio/wav'
console.log('audio', audio)
  let userBPM; // Пользовательское значение метронома
  const [startTimeState, setStateInterval] = useState("");
  const [beepFlag,setBeepFlag]=useState(false)
  const bpm = 70; // убрать когда будет UI
  const bpmInterval = 60000/bpm;
  function saveTimer() {
    setBeepFlag(!beepFlag)
    if(beepFlag){

    let startTime = Date.now();
    setStateInterval(startTime); // выкинули наверх в глобальный стейт
    const beep = () => {
      console.log('beepFlag', beepFlag)
      audio.play();
      console.log("Beeeep");
      startTime += bpmInterval;
      setStateInterval(startTime);
    };
    const myTimerSetInterval = () => setInterval(beep, bpmInterval);

    return myTimerSetInterval();
  }
  else return
  }
  function hit() {
    const hitTime = Date.now();
    const fault = bpmInterval/16;
    const firstBorder = startTimeState + bpmInterval - fault;
    const endInterval = startTimeState + bpmInterval;

    if (hitTime > startTimeState && hitTime < firstBorder) {
      console.log("SLOW NOOB");
    }
    if (hitTime >= firstBorder && hitTime <= endInterval) {
      console.log("GOD");
    }
  }

const stopBeat=()=>{
