  title="Mentronom"
  color="#432454"
  accessibilityLabel="Learn more about this purple button"
/>
      <Button
  onPress={hit}
  title="Beat"
  color="#841584"
  accessibilityLabel="Learn more about this purple button"
/>
      <Button
  onPress={stopBeat}
  title="Stop beating "
  color="#841584"
  accessibilityLabel="Learn more about this purple button"
/>
      <StatusBar style="auto" />
    </View>
  );
}

