import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import { StyleSheet, Text, View, Button, Alert, Image } from "react-native";
import expo from "./app.json";

export default function App() {
  let audio = new Audio(require("./assets/beep.wav"));
  audio.type = "audio/wav";

  let userBPM; // Пользовательское значение метронома
  const [startTimeState, setStateInterval] = useState("");

  const bpm = 70; // убрать когда будет UI
  const bpmInterval = 60000 / bpm;
  let startTimenit = Date.now();
  let startTime;
  let mainTimer;
  const myTimerSetInterval = () => {
    const beep=()=>{
      audio.play()
       startTime = ++startTimenit+ bpmInterval;  
    }
     mainTimer= setInterval(beep, bpmInterval);


}
console.log('startTime', startTime)
  function hit() {
    const hitTime = Date.now();
    const fault = bpmInterval / 16;
    const firstBorder = startTimeState + bpmInterval - fault;
    const endInterval = startTimeState + bpmInterval;

    if (hitTime > startTimeState && hitTime < firstBorder) {
      console.log("SLOW NOOB");
    }
    if (hitTime >= firstBorder && hitTime <= endInterval) {
      console.log("GOD");
    }
  }

  const stopBeat = () => {
   clearInterval(mainTimer)
  };

  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app!</Text>
      <Button
        onPress={myTimerSetInterval}
        title="Mentronom"
        color="#432454"
        accessibilityLabel="Learn more about this purple button"
      />
      <Button
        onPress={hit}
        title="Beat"
        color="#841584"
        accessibilityLabel="Learn more about this purple button"
      />
      <Button
        onPress={stopBeat}
        title="Stop beating "
        color="#841584"
        accessibilityLabel="Learn more about this purple button"
      />
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});

